import React, { useState } from "react";

function App() {
  const [modalities, setModalities] = useState([]);
  const [studiesPerDay, setStudiesPerDay] = useState(0);
  const [mbSizePerStudy, setMbSizePerStudy] = useState(0);
  const [storageNeeded, setStorageNeeded] = useState(0);

  // Add modality
  const addModality = () => {
    const newModality = {
      name: "",
      studiesPerDay: 0,
      mbSizePerStudy: 0,
    };
    setModalities([...modalities, newModality]);
  };

  // Update modality
  const updateModality = (index, field, value) => {
    const updatedModalities = [...modalities];
    updatedModalities[index][field] = value;
    setModalities(updatedModalities);
  };

  // Delete modality
  const deleteModality = (index) => {
    const updatedModalities = [...modalities];
    updatedModalities.splice(index, 1);
    setModalities(updatedModalities);
  };

  // Calculate storage needed
// Calculate storage needed
const calculateStorageNeeded = () => {
  // Convert input values to numbers
  const studiesPerDayNumber = parseInt(studiesPerDay, 10);
  const mbSizePerStudyNumber = parseFloat(mbSizePerStudy);

  // Check if conversion was successful
  if (!isNaN(studiesPerDayNumber) && !isNaN(mbSizePerStudyNumber)) {
    const totalStudiesPerYear = studiesPerDayNumber * 5 * 365;
    const totalGbNeeded = totalStudiesPerYear * mbSizePerStudyNumber;
    setStorageNeeded(totalGbNeeded);
  } else {
    // Handle invalid input values (e.g., non-numeric input)
    alert("Please enter valid numeric values for Studies Per Day and MB Size Per Study.");
  }
};


  return (
    <div>
      <h1>PACS Storage Calculator</h1>

      <div>
        <h2>Modalities</h2>
        <button onClick={addModality}>Add Modality</button>
        {modalities.map((modality, index) => (
          <div key={index}>
            <input
              type="text"
              placeholder="Modality Name"
              value={modality.name}
              onChange={(e) => updateModality(index, "name", e.target.value)}
            />
            <input
              type="number"
              placeholder="Studies Per Day"
              value={modality.studiesPerDay}
              onChange={(e) => updateModality(index, "studiesPerDay", e.target.value)}
            />
            <input
              type="number"
              placeholder="MB Size Per Study"
              value={modality.mbSizePerStudy}
              onChange={(e) => updateModality(index, "mbSizePerStudy", e.target.value)}
            />
            <button onClick={() => deleteModality(index)}>Delete</button>
          </div>
        ))}
      </div>

      <div>
        <h2>Storage Needed</h2>
        <input
          type="number"
          placeholder="Studies Per Day"
          value={studiesPerDay}
          onChange={(e) => setStudiesPerDay(e.target.value)}
        />
        <p>Storage Needed: {storageNeeded} GB</p>
        <button onClick={calculateStorageNeeded}>Calculate Storage Needed</button>
      </div>
    </div>
  );
}

export default App;
