import React, { useState, useEffect } from 'react';

function App() {
  // Sample data for the responsive table
  const tableData = [
    { label1: 'Value 1-1', label2: 'Value 1-2', label3: 'Value 1-3', label4: 'Value 1-4' },
    { label1: 'Value 2-1', label2: 'Value 2-2', label3: 'Value 2-3', label4: 'Value 2-4' },
    // Add more data items as needed
  ];

  return (
    <div className="container">
      <h2>
        Responsive Table
        <small> - Mobile-friendly</small>
      </h2>

      <ul className="responsive-table">
        {/* Table Header */}
        <li className="table-header">
          <div className="col" data-label="Column 1">Column 1</div>
          <div className="col" data-label="Column 2">Column 2</div>
          <div className="col" data-label="Column 3">Column 3</div>
          <div className="col" data-label="Column 4">Column 4</div>
        </li>

        {/* Table Rows */}
        {tableData.map((row, index) => (
          <li key={index} className="table-row">
            <div className="col" data-label="Column 1">{row.label1}</div>
            <div className="col" data-label="Column 2">{row.label2}</div>
            <div className="col" data-label="Column 3">{row.label3}</div>
            <div className="col" data-label="Column 4">{row.label4}</div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
