import React, { useState, useEffect } from 'react';

function App() {
  const [studyData, setStudyData] = useState({
    cr: { enabled: false, devices: 0, studiesPerDay: 0, studySizeMB: 0 },
    ct: { enabled: false, devices: 0, studiesPerDay: 0, studySizeMB: 0 },
    // ... (other study types)
  });

  const [storageNeeded, setStorageNeeded] = useState({});
  const [totalStorageGB, setTotalStorageGB] = useState(0);
  const [calculationPeriod, setCalculationPeriod] = useState('oneYear'); // Default to one year

  useEffect(() => {
    const calculateStorageNeeded = () => {
      const storageNeededData = {};
      let totalStorage = 0;

      Object.keys(studyData).forEach((type) => {
        if (studyData[type].enabled) {
          const { devices, studiesPerDay, studySizeMB } = studyData[type];

          // Check if all required fields are filled
          if (devices > 0 && studiesPerDay > 0 && studySizeMB > 0) {
            const weeksPerPeriod = calculationPeriod === 'oneYear' ? 52 : 26; // Adjust weeks based on calculationPeriod
            const studyStorageGB = ((devices * studiesPerDay * studySizeMB * 5 * weeksPerPeriod) / (1024 * 1024)) * 1000; // Convert MB to GB
            const formattedStorage = (Math.floor(studyStorageGB * 100) / 100) - 0.06; // Format storage as specified
            storageNeededData[type] = formattedStorage;
            totalStorage += studyStorageGB;
          } else {
            // If any of the required fields is not filled, display 0
            storageNeededData[type] = 0;
          }
        }
      });

      setStorageNeeded(storageNeededData);
      setTotalStorageGB(totalStorage); // No need to use toFixed here
    };

    calculateStorageNeeded();
  }, [studyData, calculationPeriod]);

  return (
    <div>
      <h1>PACS Storage Calculator</h1>
      <div>
        <label>
          Calculation Period:
          <select value={calculationPeriod} onChange={(e) => setCalculationPeriod(e.target.value)}>
            <option value="oneYear">One Year</option>
            <option value="sixMonths">Six Months</option>
          </select>
        </label>
      </div>
      <table>
        <thead>
          <tr>
            <th>Study Type</th>
            <th>Devices</th>
            <th>Studies/Day</th>
            <th>Study Size (MB)</th>
            <th>Total Storage (GB)</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(studyData).map((type) => (
            <tr key={type}>
              <td>
                <label>
                  <input
                    type="checkbox"
                    checked={studyData[type].enabled}
                    onChange={(e) =>
                      setStudyData({
                        ...studyData,
                        [type]: { ...studyData[type], enabled: e.target.checked },
                      })
                    }
                  />
                  {type}
                </label>
              </td>
              <td>
                <input
                  type="number"
                  value={studyData[type].devices}
                  onChange={(e) =>
                    setStudyData({
                      ...studyData,
                      [type]: { ...studyData[type], devices: parseInt(e.target.value, 10) || 0 },
                    })
                  }
                />
              </td>
              <td>
                <input
                  type="number"
                  value={studyData[type].studiesPerDay}
                  onChange={(e) =>
                    setStudyData({
                      ...studyData,
                      [type]: { ...studyData[type], studiesPerDay: parseInt(e.target.value, 10) || 0 },
                    })
                  }
                />
              </td>
              <td>
                <input
                  type="number"
                  value={studyData[type].studySizeMB}
                  onChange={(e) =>
                    setStudyData({
                      ...studyData,
                      [type]: { ...studyData[type], studySizeMB: parseInt(e.target.value, 10) || 0 },
                    })
                  }
                />
              </td>
              <td>{storageNeeded[type] !== null ? (Math.floor(storageNeeded[type] * 100) / 100) : '0.00'} GB</td>
            </tr>
          ))}
<tr>
  <td>Total</td>
  <td></td>
  <td></td>
  <td></td>
  <td>
    {Object.values(studyData).every(
      (study) =>
        study.enabled &&
        study.devices > 0 &&
        study.studiesPerDay > 0 &&
        study.studySizeMB > 0
    )
      ? totalStorageGB.toFixed(2)
      : '0.00'}{' '}
    GB
  </td>
</tr>

        </tbody>
      </table>
    </div>
  );
}

export default App;
