import React, { useState, useEffect } from 'react';
import './styles.css'; // Import your CSS file here

function App() {
  const [studyData, setStudyData] = useState({
    cr: { enabled: false, devices: 0, studiesPerDay: 0, studySizeMB: 0 },
    ct: { enabled: false, devices: 0, studiesPerDay: 0, studySizeMB: 0 },
    dx: { enabled: false, devices: 0, studiesPerDay: 0, studySizeMB: 0 },
    mg: { enabled: false, devices: 0, studiesPerDay: 0, studySizeMB: 0 },
    mr: { enabled: false, devices: 0, studiesPerDay: 0, studySizeMB: 0 },
    us: { enabled: false, devices: 0, studiesPerDay: 0, studySizeMB: 0 },
    xa: { enabled: false, devices: 0, studiesPerDay: 0, studySizeMB: 0 },
  });

  const [storageNeeded, setStorageNeeded] = useState({});
  const [totalStorageGB, setTotalStorageGB] = useState(0);
  const [calculationPeriod, setCalculationPeriod] = useState('oneYear'); // Default to one year

  useEffect(() => {
    const calculateStorageNeeded = () => {
      const storageNeededData = {};
      let totalStorage = 0;

      Object.keys(studyData).forEach((type) => {
        if (studyData[type].enabled) {
          const { devices, studiesPerDay, studySizeMB } = studyData[type];
          const weeksPerPeriod = calculationPeriod === 'oneYear' ? 52 : 26; // Adjust weeks based on calculationPeriod
          const studyStorageGB = ((devices * studiesPerDay * studySizeMB * 5 * weeksPerPeriod) / (1024 * 1024)) * 1000; // Convert MB to GB
          const formattedStorage = (Math.floor(studyStorageGB * 100) / 100); // Format storage as specified
          storageNeededData[type] = formattedStorage;
          totalStorage += formattedStorage; // Accumulate formattedStorage here
        }
      });

      setStorageNeeded(storageNeededData);
      setTotalStorageGB(totalStorage); // Set totalStorage to the accumulated value
    };

    calculateStorageNeeded();
  }, [studyData, calculationPeriod]);

  return (
    <div className="container">
      <h2>
        PACS Storage Calculator
      </h2>
      <div>
        <label>
          Calculation Period:
          <select value={calculationPeriod} onChange={(e) => setCalculationPeriod(e.target.value)}>
            <option value="oneYear">One Year</option>
            <option value="sixMonths">Six Months</option>
          </select>
        </label>
      </div>
      <ul className="responsive-table">
        <li className="table-header">
          <div className="col" data-label="Study Type">Study Type</div>
          <div className="col" data-label="Devices">Devices</div>
          <div className="col" data-label="Studies/Day">Studies/Day</div>
          <div className="col" data-label="Study Size (MB)">Study Size (MB)</div>
          <div className="col" data-label="Total Storage (GB)">Total Storage (GB)</div>
        </li>
        {Object.keys(studyData).map((type) => (
          <li key={type} className="table-row">
            <div className="col" data-label="Study Type">
              <label>
                <input
                  type="checkbox"
                  checked={studyData[type].enabled}
                  onChange={(e) =>
                    setStudyData({
                      ...studyData,
                      [type]: { ...studyData[type], enabled: e.target.checked },
                    })
                  }
                />
                {type}
              </label>
            </div>
            <div className="col" data-label="Devices">
              <input
                type="number"
                value={studyData[type].devices}
                onChange={(e) =>
                  setStudyData({
                    ...studyData,
                    [type]: { ...studyData[type], devices: parseInt(e.target.value, 10) || 0 },
                  })
                }
              />
            </div>
            <div className="col" data-label="Studies/Day">
              <input
                type="number"
                value={studyData[type].studiesPerDay}
                onChange={(e) =>
                  setStudyData({
                    ...studyData,
                    [type]: { ...studyData[type], studiesPerDay: parseInt(e.target.value, 10) || 0 },
                  })
                }
              />
            </div>
            <div className="col" data-label="Study Size (MB)">
              <input
                type="number"
                value={studyData[type].studySizeMB}
                onChange={(e) =>
                  setStudyData({
                    ...studyData,
                    [type]: { ...studyData[type], studySizeMB: parseInt(e.target.value, 10) || 0 },
                  })
                }
              />
            </div>
            <div className="col" data-label="Total Storage (GB)">{storageNeeded[type] || '0.00'} GB</div>
          </li>
        ))}
        <li className="table-row">
          <div className="col" data-label="Study Type">Total</div>
          <div className="col" data-label="Devices"></div>
          <div className="col" data-label="Studies/Day"></div>
          <div className="col" data-label="Study Size (MB)"></div>
          <div className="col" data-label="Total Storage (GB)">{totalStorageGB.toFixed(2)} GB</div>
        </li>
      </ul>
    </div>
  );
}

export default App;
